        transformer._transform = tranformFn;
        return transformer;
    }

    static async makePipeline(streams, onDataFn=null){
        return new Promise((resolve, reject)=>{
            let writer = streams.slice(-1)[0];
            let reader = streams[0];
            writer.on('finish',()=>{
                resolve('success');
            });
            writer.on('error',(err)=>{
                reject(`Pipeline failed. ${err}`);
