        let samples = [];
        const GetUtilEnough = Sampling.makeCheckEnough(size);
        let idx = -1;
        while(GetUtilEnough(samples)){
            idx = random.int(0, domain.length-1);
            if(domain[idx]!==null){
                samples = [...samples, domain[idx]];
                if(!replace){
                    domain[idx]=null;//non replaceable
                }
            }
        }    
        return samples;
    }
}
